const decrypt = async ( payload ) => {
    try {
        const keystore = jose.JWK.createKeyStore();
        const decProps = {
            kid: VISA_MLE_KEY_ID,
            alg: 'RSA-OAEP-256',
            enc: 'A128GCM'
        };

        const decryptionKey = await fsp.readFile(VISA_MLE_KEY);
        const key = await keystore.add(decryptionKey, 'pem', decProps);
        const data = await jose.JWE.createDecrypt(key).decrypt(payload)
        return data;
    } catch (error) {
        node.error(error.message, msg);
    }
}

