module.exports = {
  visaApiEnvConfig: () => {
    const config = {
      API_KEY: process.env.API_KEY,
      VISA_API_URL: process.env.VISA_API_URL,
      SHARED_SECRET: process.env.SHARED_SECRET,
    };

    Object.entries(config).forEach(([key, value]) => {
      if (value === undefined) {
        throw new Error(`${key} not configured in Environment Variables`);
      }
    });

    return config;
  },
};
